import axios from "axios";

export const axiosWithAuth = () => {
  const token = JSON.parse(localStorage.getItem("authToken"));
  console.log("axiosWithAuth Token: ", token);
  return axios.create({
    baseURL: "http://localhost:5000/api",
    header: {
      Authorization: token
    }
  });
};
